$(".ranking-fieldset #region").change(function(){
    var region = $(".ranking-fieldset #region option:selected").val();
    if(region=="default"){
        getAllByAggs("prefecture.keyword",100,null,"",2);
    }else{
        getAllByAggs("prefecture.keyword",100,"région.keyword",region,2);
    }
});

