 $(".tags a").click(function(){
    console.log("href:"+$(this).attr("href"));
    var pos = $("."+$(this).attr("href").substr(1)).offset().top;
    $('html,body').animate(
         {
          scrollTop: pos
     },
    'slow');   
});

