screen.script:
          removeLastRootPage(userName);
          loadPageBytype(0);
          
          var tabInd = ["1-2019"];

          restGetAllCommue([["trim"],["1-2019"]],"indecators.score","desc",60,0,0);
          restGetAllCommue([["trim"],["1-2019"]],"indecators.delai","desc",3,0,1);
          restGetAllCommue([["trim"],["1-2019"]],"indecators.attractivite","desc",3,0,2);
          restGetAllCommue([["trim"],["1-2019"]],"indecators.digital","desc",3,0,3);
          restGetAllCommue([["trim"],["1-2019"]],"indecators.ecosystem","desc",3,0,4);
          restGetAllCommue([["trim"],["1-2019"]],"indecators.fiscalite","desc",3,0,5);
          restGetAllCommue([["trim"],["1-2019"]],"indecators.scoreU","desc",10,0,6);
          restGetAllCommue([["trim"],["1-2019"]],"indecators.scoreE","desc",10,0,7);

          getAllByAggs("trim.keyword",100,null,"",0);
          getAllByAggs("région.keyword",100,null,"",1);
          getAllByAggs("préfecture.keyword",100,null,"",2);

          $("#ranking-table .fa-sort-down").click(function(){
            
            var classe = $(this).parent().attr("class");
            
            $(this).parent().find(".fa-sort-up").show();
            $("#ranking-table .fa-sort-down,#ranking-table .fa-sort-up").removeClass("blue-icon");
            $(this).parent().find(".fa-sort-up").addClass("blue-icon");
            
            $(this).hide();

            if(classe=="rank-dl"){
              restGetAllCommue(getFiltersArray(tabInd),"indecators.delai","asc",60,0,0)
            }else if(classe=="rank-dg"){
              restGetAllCommue(getFiltersArray(tabInd),"indecators.digital","asc",60,0,0)
            }else if(classe=="rank-at"){
              restGetAllCommue(getFiltersArray(tabInd),"indecators.attractivite","asc",60,0,0)
            }else if(classe=="rank-es"){
              restGetAllCommue(getFiltersArray(tabInd),"indecators.ecosystem","asc",60,0,0)
            }else if(classe=="rank-fs"){
              restGetAllCommue(getFiltersArray(tabInd),"indecators.fiscalite","asc",60,0,0)
            }else if(classe=="sp-td"){
              restGetAllCommue(getFiltersArray(tabInd),"indecators.score","asc",60,0,0)
            }
          });

          $("#ranking-table .fa-sort-up").click(function(){
            
            var classe = $(this).parent().attr("class");
            
            $(this).parent().find(".fa-sort-down").show();
            $("#ranking-table .fa-sort-down,#ranking-table .fa-sort-up").removeClass("blue-icon");
            $(this).parent().find(".fa-sort-down").addClass("blue-icon");
            
            $(this).hide();

            if(classe=="rank-dl"){
              restGetAllCommue(getFiltersArray(tabInd),"indecators.delai","desc",60,0,0)
            }else if(classe=="rank-dg"){
              restGetAllCommue(getFiltersArray(tabInd),"indecators.digital","desc",60,0,0)
            }else if(classe=="rank-at"){
              restGetAllCommue(getFiltersArray(tabInd),"indecators.attractivite","desc",60,0,0)
            }else if(classe=="rank-es"){
              restGetAllCommue(getFiltersArray(tabInd),"indecators.ecosystem","desc",60,0,0)
            }else if(classe=="rank-fs"){
              restGetAllCommue(getFiltersArray(tabInd),"indecators.fiscalite","desc",60,0,0)
            }else if(classe=="sp-td"){
              restGetAllCommue(getFiltersArray(tabInd),"indecators.score","desc",60,0,0)
            }
          });
          
          $(".ranking-fieldset #region").change(function(){
              var region = $(".ranking-fieldset #region option:selected").val();
              if(region=="default"){
                  getAllByAggs("préfecture.keyword",100,null,"",2);
              }else{
                  getAllByAggs("préfecture.keyword",100,"région.keyword",region,2);
              }
          });

          $(".ranking-fieldset #period,.ranking-fieldset #region,.ranking-fieldset #prefecture").change(function(){
              var trim = $(".ranking-fieldset #period option:selected").val();
              var region = $(".ranking-fieldset #region option:selected").val();
              var prefecture = $(".ranking-fieldset #prefecture option:selected").val();

              var filters = [];
              var filtersBy = [];

              if(trim=="default"){
                filters.push("trim.keyword");
                filtersBy.push("1-2019");
              }else{
                filters.push("trim.keyword");
                filtersBy.push(trim);
              }

              if(region!="default"){
                filters.push("région.keyword");
                filtersBy.push(region);
              }

              if(prefecture!="default"){
                filters.push("préfecture.keyword");
                filtersBy.push(prefecture);
              }
              
              tabInd = [];
              tabInd.push(trim);
              tabInd.push(region);
              tabInd.push(prefecture);

              restGetAllCommue([filters,filtersBy],"indecators.score","desc",60,0,0);


          });

        drawRadarRegion();
